<?php### VLADISLAV BOGDANOV ###### 	ASO - M06      ###### 	 ASIX 2        ###//INICIAMOS UNA SESION, EN ESTE CASO CON NUESTRO USUARIO LINUXsession_start();//CREAMOS CLASE CONTROLLER PARA PODER ENVIAR DEPENDIENDO DEL REQUISITO ELEGIDO A DIFERENTES APARTADOSclass Controller {//CREAMOS VARIEBLE PÚBLICA MODEL	public $model;	//CREAMOS FUNCION PUBLICA CONSTRUCT EN EL CONTROLER (VACIA)	public function __construct()  {      } #############################################INVOKE FUNCION###########################################	public function invoke() {//PREGUNTAR DE LA PAGINA PRINCIPAL QUE OPCION SE ELIGE		if (isset($_REQUEST['principal'])) {			unset($_SESSION['option']);		}//DEPENDE DE LA OPCION REENVIAMOS A SU VARIABLE REQUEST DEPENDIENDO DE LA SESION DEL USUARIO QUE ESTE INICIADO.		if (isset($_REQUEST['option'])) {			$_SESSION['option']=$_REQUEST['option'];		}//!ISSET SI NO SE HA SELECCIONADO OPCION, ENVIAR DE VUELTA A LA MAIN PAGE, SI SE SELECCIONA OPCION ENTONCES ELSE SWITCH Y ENTRAMOS EN LA FUNCION SELECCIONADA		if (!isset($_SESSION['option']))		{			// web por defecto			include 'view/main_page.php';		}//SI SE HA SELECCIONADO UNA OPCION		else		{			//DEPENDIENDO DEL CASO				switch($_SESSION['option']) {			//CASO SELECCIONADO			case 'create_user':				//INCLUYE EL CONTROLLER DE ESTE CASO				include 'controller/controller_create_user.php';				//CREA LA CLASE E INVOCA EN LA VARIABLE CONTROLLER				$controller = new controller_create_user();				$controller->invoke();				//ACABA			break;						//CASO SELECCIONADO			case 'list_user':				//INCLUYE EL CONTROLLER DE ESTE CASO				include 'controller/controller_list_user.php';				//CREA LA CLASE E INVOCA EN LA VARIABLE CONTROLLER				$controller = new controller_list_user();				$controller->invoke();			//ACABA			break;			//CASO SELECCIONADO			case 'create_group':				//INCLUYE EL CONTROLLER DE ESTE CASO				include 'controller/controller_create_group.php';				//CREA LA CLASE E INVOCA EN LA VARIABLE CONTROLLER				$controller = new controller_create_group();				$controller->invoke();				//ACABA			break;			//CASO SELECCIONADO			case 'list_groups':				//INCLUYE EL CONTROLLER DE ESTE CASO				include 'controller/controller_list_group.php';				//CREA LA CLASE E INVOCA EN LA VARIABLE CONTROLLER				$controller = new controller_list_group();				$controller->invoke();				//ACABA			break;			//CASO SELECCIONADO			case 'create_user_file':				//INCLUYE EL CONTROLLER DE ESTE CASO				include 'controller/controller_create_user_file.php';				//CREA LA CLASE E INVOCA EN LA VARIABLE CONTROLLER				$controller = new controller_list_group();				$controller->invoke();				//ACABA			break;			}		}	}}?>